import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const EVENT_DATE = new Date("2026-03-14T09:00:00");

function useCountdown(targetDate) {
  const [timeLeft, setTimeLeft] = useState(getTimeLeft(targetDate));

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(getTimeLeft(targetDate));
    }, 1000);
    return () => clearInterval(timer);
  }, [targetDate]);

  return timeLeft;
}

function getTimeLeft(targetDate) {
  const now = new Date();
  const diff = Math.max(0, targetDate - now);

  return {
    days: Math.floor(diff / (1000 * 60 * 60 * 24)),
    hours: Math.floor((diff / (1000 * 60 * 60)) % 24),
    minutes: Math.floor((diff / (1000 * 60)) % 60),
    seconds: Math.floor((diff / 1000) % 60),
  };
}

export default function Home() {
  const navigate = useNavigate();
  const { days, hours, minutes, seconds } = useCountdown(EVENT_DATE);

  // ðŸŒŒ PARALLAX
  useEffect(() => {
    const handleScroll = () => {
      const depth1 = document.querySelector(".depth-1");
      const depth2 = document.querySelector(".depth-2");
      const depth3 = document.querySelector(".depth-3");

      const scrollY = window.scrollY;

      if (depth1) depth1.style.transform = `translateY(${scrollY * 0.1}px)`;
      if (depth2) depth2.style.transform = `translateY(${scrollY * 0.2}px)`;
      if (depth3) depth3.style.transform = `translateY(${scrollY * 0.35}px)`;
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // ðŸŒ  Shooting Stars
  useEffect(() => {
    const createStar = () => {
      const star = document.createElement("div");
      star.className = "shooting-star";

      star.style.left = Math.random() * window.innerWidth + "px";
      star.style.top = Math.random() * window.innerHeight + "px";

      document.querySelector(".star-layer")?.appendChild(star);

      setTimeout(() => star.remove(), 3000);
    };

    const interval = setInterval(createStar, 1800);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-wrapper">

      {/* ðŸŒŒ Background Engine */}
      <div className="background-engine">
        <div className="depth-1"></div>
        <div className="depth-2"></div>
        <div className="depth-3"></div>
        <div className="star-layer"></div>
        <div className="particle-layer"></div>
      </div>

      {/* NAVBAR */}
      <nav className="navbar fade-in">
        <div className="nav-inner">
          <div className="nav-logo">ASTRYX'26</div>

          <div className="nav-center">
            <a href="/events">Events</a>
            <a href="#workshops">Workshops</a>
            <a href="#contact">Contact</a>
          </div>

          <button
            className="login-btn"
            onClick={() => navigate("/login")}
          >
            Login
          </button>
        </div>
      </nav>

      {/* HERO */}
      <section className="hero fade-in-up">
        <div className="hero-content">

          <div className="inst-row">
            <img src="/logo.png" alt="RIT Logo" className="inst-logo" />

            <div className="inst-text">
              <h2 className="top-info">
                RAMCO INSTITUTE OF TECHNOLOGY
              </h2>

              <p className="inst-sub">
                (An Autonomous Institution)
              </p>

              <p className="inst-desc">
                Approved by AICTE, New Delhi & Affiliated to Anna University<br />
                NAAC Accredited with 'A+' Grade & ISO 9001:2015 Certified Institution<br />
                NBA Accredited UG Programs: CSE, EEE, ECE, MECH and CIVIL
              </p>
            </div>

            <img src="/logo2.png" alt="IE Logo" className="inst-logo" />
          </div>

          <p className="dept">
            DEPARTMENT OF INFORMATION TECHNOLOGY
          </p>

          <p className="assoc">
            in association with <b>IE (I) IT Student Chapter</b>
          </p>

          <h1 className="main-title">ASTRYX'26</h1>

          <div className="event-info">
            MARCH 14, 2026 â€¢ LAST DATE TO REGISTER: 05/03/2026
          </div>

          <button
            className="explore-btn"
            onClick={() => navigate("/events")}
          >
            Explore Events
          </button>

          <div className="countdown-container">
            <CountdownBox value={days} label="DAYS" />
            <CountdownBox value={hours} label="HOURS" />
            <CountdownBox value={minutes} label="MINUTES" />
            <CountdownBox value={seconds} label="SECONDS" />
          </div>

        </div>
      </section>

      {/* ABOUT */}
      <section className="about-section">
        <div className="about-container">
          <h2 className="about-title">About Astryx '26</h2>

          <p className="about-description">
            Astryx â€™26 is the flagship National Level Technical Symposium organised
            by the Department of Information Technology at Ramco Institute of Technology.
          </p>

          <div className="about-highlights">
            <div className="highlight-card">
              <h3>10+ Events</h3>
              <p>Technical & Non-Technical Competitions</p>
            </div>

            <div className="highlight-card">
              <h3>Workshops</h3>
              <p>Hands-on Industry Sessions</p>
            </div>

            <div className="highlight-card">
              <h3>Exciting Prizes</h3>
              <p>Cash Awards & Certificates</p>
            </div>
          </div>
        </div>
      </section>

    </div>
  );
}

function CountdownBox({ value, label }) {
  return (
    <div className="countdown-box">
      <div className="count-value">
        {String(value).padStart(2, "0")}
      </div>
      <div className="count-label">{label}</div>
    </div>
  );
}
